        static int Day1_Floor(string input)
        {
            return input.Where(x => x.ToString() == "(").Count() - input.Where(x => x.ToString() == ")").Count();
        }

        static int Day1_Position(string input)
        {
            int position = 1;
            for(int x = 0; x < input.Length; x++)
            {
                if (input[x].ToString() == "(")
                    position++;
                else
                    position--;
                if (position == -1)
                    return x;
            }
            return 0;
        }

        static string Day2()
        {
            int totalWrapping = 0;
            int ribbon = 0;
            string nextLine;

            StreamReader stream = new StreamReader(@"Day2_Input.txt");
            while((nextLine = stream.ReadLine()) != null)
            {
                MatchCollection resultString = Regex.Matches(nextLine, @"\d+");
                List<Match> matchList = resultString.Cast<Match>().OrderBy(x => Convert.ToInt32(x.Value)).ToList();

                //wrapping
                int sideOne = Convert.ToInt32(resultString[0].Value) * Convert.ToInt32(resultString[1].Value) * 2;
                int sideTwo = Convert.ToInt32(resultString[1].Value) * Convert.ToInt32(resultString[2].Value) * 2;
                int sideThree = Convert.ToInt32(resultString[0].Value) * Convert.ToInt32(resultString[2].Value) * 2;
                int scrapWrap = Convert.ToInt32(matchList[0].Value) * Convert.ToInt32(matchList[1].Value);

                //ribbon
                int shortOne = Convert.ToInt32(matchList[0].Value) * 2;
                int shortTwo = Convert.ToInt32(matchList[1].Value) * 2;
                int bow = Convert.ToInt32(matchList[0].Value) * Convert.ToInt32(matchList[1].Value) * Convert.ToInt32(matchList[2].Value);

                totalWrapping += sideOne + sideTwo + sideThree + scrapWrap;
                ribbon += shortOne + shortTwo + bow;
            }
            stream.Close();

            return "Wrapping: " + totalWrapping + " | Ribbon: " + ribbon;
        }

        static int Day3(string input)
        {
            List<System.Drawing.Point> houseList = new List<System.Drawing.Point>();
            int xLoc = 0;
            int yLoc = 0;
            int roboXLoc = 0;
            int roboYLoc = 0;

            for (int x = 0; x < input.Length; x++)
            {
                if (!houseList.Contains(new System.Drawing.Point(xLoc, yLoc)))
                    houseList.Add(new System.Drawing.Point(xLoc, yLoc));
                if (!houseList.Contains(new System.Drawing.Point(roboXLoc, roboYLoc)))
                    houseList.Add(new System.Drawing.Point(roboXLoc, roboYLoc));

                switch (input[x].ToString())
                {
                    case "^":
                        if (x % 2 == 0)
                            yLoc++;
                        else
                            roboYLoc++;
                        break;
                    case ">":
                        if (x % 2 == 0)
                            xLoc++;
                        else
                            roboXLoc++;
                        break;
                    case "v":
                        if (x % 2 == 0)
                            yLoc--;
                        else
                            roboYLoc--;
                        break;
                    case "<":
                        if (x % 2 == 0)
                            xLoc--;
                        else
                            roboXLoc--;
                        break;
                }
            }

            return houseList.Count;
        }

        static int Day4(string input)
        {
            for(int x = 0; ; x++)
            {
                string originalInput = input;
                string newInput = input + x.ToString();
                Console.WriteLine(x);

                MD5 md5 = MD5.Create();
                byte[] inputBytes = System.Text.Encoding.ASCII.GetBytes(newInput);
                byte[] hash = md5.ComputeHash(inputBytes);

                // step 2, convert byte array to hex string
                StringBuilder sb = new StringBuilder();
                for (int i = 0; i < hash.Length; i++)
                {
                    sb.Append(hash[i].ToString("x2"));
                }

                if (sb.ToString().Substring(0, 6) == "000000")
                    return x;
            }

            //return sb.ToString();

            //return 0;
        }

        static int Day5()
        {
            string nextLine;
            int niceCnt = 0;

            StreamReader stream = new StreamReader(@"Day5_Input.txt");
            while((nextLine = stream.ReadLine()) != null)
            {
                int vowelMatch = (Regex.Match(nextLine, @"[aeiou]+").Success) ? Regex.Matches(nextLine, @"[aeiou]+").Count : 0;
                bool repeatMatch = Regex.Match(nextLine, @"(.)\1").Success;
                bool badStrings = Regex.Match(nextLine, @"([ab]{2})([xy]{2})").Success;

                if (vowelMatch >= 3 && repeatMatch == true)
                    niceCnt++;
            }

            return niceCnt;
        }
    }
